import axios from "axios";
import { useEffect, useState } from "react";
import Loading from "./Loading";
import { useSelector, useDispatch } from "react-redux";
import { setPeople } from "../features/people/peopleSlice"
import { Typography, Card, List, Image, message } from "antd";
import "./index.css"
import InfiniteScroll from 'react-infinite-scroller';
export default function People() {

    const auth = useSelector((state) => state.auth);
    const people = useSelector((state) => state.people);
    const dispatch = useDispatch();
    const [index, setIndex] = useState(0);
    const [loading, setLoading] = useState(false);
    const [hasMore, setHasMore] = useState(true);
    useEffect(() => {
        if (auth.authorization) {
            axios.get("https://dk-react-backend.herokuapp.com/users")
                .then(res => { dispatch(setPeople(res.data)); setIndex(12); })
                .catch(error => console.error(error));
        }

    }, [auth.authorization])

    if (auth.authorization === false) {
        return <Typography.Text>To see people's profiles, you have to log in first</Typography.Text>
    }

    if (people === null) {
        return <Loading />;
    }

    function handleInfiniteOnLoad() {


        if (index + 4 >= people.length) {
            message.warning('Load all people\'s profile');
            setIndex(people.length);
            setLoading(false);
            setHasMore(false);
            return;
        }

        setLoading(true);
        // setLoading(false); setIndex(index => index + 4);
        setTimeout(() => { setLoading(false); setIndex(index => index + 4); }, 1500)

    };
    const displayPeople = people.slice(0, index);
    return <>
        <div className="textcenter">
            <Typography.Title level={2}>All people </Typography.Title >
        </div>
        <InfiniteScroll
            initialLoad={true}
            pageStart={0}
            loadMore={handleInfiniteOnLoad}
            hasMore={(!loading) && hasMore}
            useWindow={true}
        >
            <List
                grid={
                    {
                        gutter: 20,
                        xs: 1,
                        sm: 2,
                        md: 3,
                        lg: 3,
                        xl: 4,
                        xxl: 4,

                    }
                }
                dataSource={displayPeople}
                renderItem={item => (
                    <List.Item>
                        <Card
                            hoverable={false}

                            cover={<Image
                                height={350}
                                src={item.profile_picture_url !== null ? item.profile_picture_url : ''}
                                fallback="data:image/png;base64,"
                            />}
                        >
                            <Card.Meta title={`${item.first_name} ${item.last_name}`} description={item.email} />
                        </Card>
                    </List.Item>
                )}

            >{loading && hasMore ? (
                <Loading />
            ) : ""}   </List>
        </InfiniteScroll>

    </>
}